#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
    /* Loop forever */
//	Fetching the address from the user manuals and reference manual
	uint32_t volatile *pRCCAHB1 = (uint32_t *)0x40023830;
	uint32_t volatile *pGPIODmode = (uint32_t *)0x40020c00;
	uint32_t volatile *pGPIOoutputdata = (uint32_t *)0x40020c14;

//	uint32_t *pGPIOAReg = (uint32_t *)0x40020000;
	uint32_t volatile *pGPIOInputReg = (uint32_t *)0x40020010;




//	1. Enabling the clock for GPIOD peripheral in the ANB1EN
//	*pRCCAHB1 |= 0x08;
	*pRCCAHB1 |= (1 << 3);
//	2. Enabling the clock for GPIOA peripheral in the AHB1EN
	*pRCCAHB1 |= 0x1;

//	2.Configure the mode of the IO pin at output
//	a.clear the 24th and 25th bit positions (clear)
//	*pGPIODmode &= 0xFCFFFFFF;
	*pGPIODmode &= (0 << 24);
	*pGPIODmode &= (0 << 25);

//	b.make the 24th bit position as SET(!)
//	*pGPIODmode |= 0x01000000;
	*pGPIODmode |= (1 << 24);



//	3. Set the 12th bit of the Output data as SET(!)
//	*pGPIOoutputdata |= 0x00001000;
	while(1){

		uint32_t statusReg = *pGPIOInputReg & 1;

		if(statusReg){
			*pGPIOoutputdata |= (1 << 12);
		}

//		//delay
//		for(uint32_t i = 0; i < 3100000; i++);

		//OFF
		else{
			*pGPIOoutputdata &= ~(1 << 12);
		}

//		//delay
//		for(uint32_t i = 0; i < 3100000; i++);
	}

//	for(;;);
}
